import React from 'react'
import {
    StyleSheet,
    View,
    Dimensions,
    Image,
    Text,
    TouchableOpacity,
  } from "react-native";


export default function PlacesBitmoji() {
  return (
    <View style={styles.bitmojiContainer}>
        <View style={styles.myBitmoji}>
        <Image
            style={styles.bitmojiImage}
            source={require("../../assets/snapchat/personalBitmoji.png")}
        />
        <View style={styles.bitmojiTextContainer}>
            <Text style={styles.bitmojiText}>My Bitmoji</Text>
        </View>
        </View>
        <View style={styles.places}>
        <Image
            style={styles.bitmojiImage}
            source={require("../../assets/snapchat/personalBitmoji.png")}
        />
        <View style={styles.bitmojiTextContainer}>
            <Text style={styles.bitmojiText}>Places</Text>
        </View>
        </View>
        <View style={styles.myFriends}>
        <Image
            style={styles.bitmojiImage}
            source={require("../../assets/snapchat/personalBitmoji.png")}
        />
        <View style={styles.bitmojiTextContainer}>
            <Text style={styles.bitmojiText}>Friends</Text>
        </View>
        </View>
    </View>
  )
}



const styles = StyleSheet.create({
    screenContainer: {
      flex: 1,
      backgroundColor: "#fff",
      alignItems: "center",
      justifyContent: "center",
    },
    statusBar:{
      width: "100%",
      height: 50,
      backgroundColor:"red",
      position: "absolute",
      top:40,

      zIndex:100

    },
    map: {
      width: Dimensions.get("window").width,
      height: Dimensions.get("window").height,
    },
    locationContainer: {
      position: "absolute",
      bottom: 100,
      width: "100%",
      height: 30,
      alignItems: "center",
    },
    userLocation: {
      backgroundColor: "white",
      borderRadius: 15,
      height: 30,
      width: 30,
      alignItems: "center",
      justifyContent: "center",
      marginLeft: 8,
    },
    bitmojiContainer: {
      width: "100%",
      height: 70,
      position: "absolute",
      bottom: 10,
      flexDirection: "row",
      justifyContent: "space-between",
    },
    myBitmoji: {
      width: 70,
      height: 70,
      alignItems: "center",
      justifyContent: "center",
      marginLeft: 5,
    },
    bitmojiImage: {
      width: 50,
      height: 50,
    },
    bitmojiTextContainer: {
      backgroundColor: "white",
      borderRadius: 20,
      padding: 4,
    },
    bitmojiText: {
      fontSize: 10,
      fontWeight: "700",
    },
    places: {
      width: 70,
      height: 70,
      alignItems: "center",
      justifyContent: "center",
    },
    myFriends: {
      width: 70,
      height: 70,
      alignItems: "center",
      justifyContent: "center",
    },
  });
